import React, { useState, useEffect, useContext } from 'react'; // Добавлен useContext
import { motion } from 'framer-motion';
import { toast } from 'react-toastify';
import { FaList, FaFilter, FaSync, FaEdit, FaTrashAlt, FaPaperPlane, FaCheckCircle, FaTimesCircle, FaHourglassHalf, FaBan, FaUserTie } from 'react-icons/fa'; // Добавлена FaUserTie
import {
  getMyVacations,
  getAllVacations, // Добавлен импорт
  cancelVacationRequest,
  approveVacationRequest, // Добавлен импорт
  rejectVacationRequest // Добавлен импорт
} from '../../api/vacations';
import Loader from '../../components/ui/Loader/Loader';
import { UserContext } from '../../context/UserContext'; // Добавлен импорт UserContext
// import './VacationsList.css';

// Константы статусов (можно вынести в отдельный файл)
const STATUS = {
  DRAFT: 1,
  PENDING: 2,
  APPROVED: 3,
  REJECTED: 4,
  CANCELLED: 5,
};

const STATUS_MAP = {
  [STATUS.DRAFT]: { text: 'Черновик', class: 'status-draft', Icon: FaEdit },
  [STATUS.PENDING]: { text: 'На рассмотрении', class: 'status-pending', Icon: FaHourglassHalf },
  [STATUS.APPROVED]: { text: 'Утверждена', class: 'status-approved', Icon: FaCheckCircle },
  [STATUS.REJECTED]: { text: 'Отклонена', class: 'status-rejected', Icon: FaTimesCircle },
  [STATUS.CANCELLED]: { text: 'Отменена', class: 'status-cancelled', Icon: FaBan },
};

// Функция для форматирования даты
const formatDate = (dateString) => {
  if (!dateString) return '-';
  try {
    // Используем ISO формат для большей надежности парсинга
    return new Date(dateString).toLocaleDateString('ru-RU');
  } catch (e) {
    return dateString; // Возвращаем исходную строку в случае ошибки
  }
};

// Компонент для отображения статуса с использованием STATUS_MAP
const StatusBadge = ({ statusId, statusName }) => { // Принимаем и statusName
  const statusInfo = STATUS_MAP[statusId] || { text: statusName || 'Неизвестно', class: 'status-unknown', Icon: FaHourglassHalf };
  const { text, class: statusClass, Icon } = statusInfo;

  return (
    <span className={`status-badge ${statusClass}`}>
      <Icon style={{ marginRight: '5px', verticalAlign: 'middle' }} />
      {text}
    </span>
  );
};


const VacationsList = () => {
  const { user } = useContext(UserContext); // Получаем данные пользователя из контекста
  const [vacations, setVacations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [year, setYear] = useState(new Date().getFullYear());
  const [statusFilter, setStatusFilter] = useState(''); // Состояние для фильтра статуса ('', '1', '2', ...)
  const [error, setError] = useState(null);

  const isAdmin = user?.isAdmin; // Возвращаем camelCase для user context
  const isManager = user?.isManager; // Возвращаем camelCase для user context

  // Функция загрузки заявок в зависимости от роли и фильтров
  const fetchVacations = async (selectedYear, selectedStatus) => {
    setLoading(true);
    setError(null);
    const currentStatusFilter = selectedStatus === '' ? null : parseInt(selectedStatus);

    try {
      let data;
      const filters = { year: selectedYear };
      if (currentStatusFilter !== null) {
          filters.status = currentStatusFilter;
      }

      if (isAdmin || isManager) {
        // Админ видит все, Менеджер видит свой отдел (фильтрация по отделу на бэке)
        data = await getAllVacations(filters);
      } else {
        // Обычный пользователь видит только свои
        data = await getMyVacations(selectedYear, currentStatusFilter);
      }
      // Убираем промежуточную обработку processedData, будем использовать поля напрямую в JSX
      setVacations(data || []); // Устанавливаем пустой массив, если data undefined
    } catch (err) {
      setError(err.message || 'Не удалось загрузить список заявок.');
      toast.error(err.message || 'Не удалось загрузить список заявок.');
    } finally {
      setLoading(false);
    }
  };

  // Загрузка данных при монтировании и смене года или статуса
  useEffect(() => {
    if (user) { // Загружаем только если пользователь определен
        fetchVacations(year, statusFilter);
    }
  }, [year, statusFilter, user]); // Добавлены statusFilter и user в зависимости

  // Обработчик смены года
  const handleYearChange = (e) => {
    setYear(parseInt(e.target.value));
  };

  // Обработчик смены фильтра статуса
  const handleStatusChange = (e) => {
    setStatusFilter(e.target.value);
  };

  // Обработчик отмены заявки
  const handleCancel = async (id) => {
    if (window.confirm('Вы уверены, что хотите отменить эту заявку?')) {
      setLoading(true);
      try {
        await cancelVacationRequest(id);
        toast.success('Заявка успешно отменена');
        fetchVacations(year, statusFilter); // Обновляем список
      } catch (err) {
        toast.error(err.message || 'Не удалось отменить заявку.');
        setLoading(false); // Разблокируем кнопки в случае ошибки
      }
    }
  };

  // Обработчик утверждения заявки
  const handleApprove = async (id) => {
      if (window.confirm('Вы уверены, что хотите утвердить эту заявку?')) {
          setLoading(true);
          try {
              await approveVacationRequest(id);
              toast.success('Заявка успешно утверждена');
              fetchVacations(year, statusFilter); // Обновляем список
          } catch (err) {
              toast.error(err.message || 'Не удалось утвердить заявку.');
              setLoading(false);
          }
      }
  };

  // Обработчик отклонения заявки
  const handleReject = async (id) => {
      const reason = prompt('Укажите причину отклонения (необязательно):');
      // Если пользователь нажал "Отмена" в prompt, reason будет null
      if (reason !== null) {
          if (window.confirm(`Вы уверены, что хотите отклонить эту заявку? ${reason ? `Причина: ${reason}` : ''}`)) {
              setLoading(true);
              try {
                  await rejectVacationRequest(id, reason || '');
                  toast.success('Заявка успешно отклонена');
                  fetchVacations(year, statusFilter); // Обновляем список
              } catch (err) {
                  toast.error(err.message || 'Не удалось отклонить заявку.');
                  setLoading(false);
              }
          }
      }
  };

  // Функция проверки, может ли текущий пользователь управлять заявкой (утверждать/отклонять)
  const canManageRequest = (vacationOwnerId) => { // Переименован параметр для ясности
      if (!user) return false;
      if (isAdmin) return true; // Админ может всегда
      // Менеджер может управлять заявками (проверка отдела на бэке)
      if (isManager) return true;
      return false;
  };

  return (
    <motion.div
      className="vacations-list-container card"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <h2><FaList /> Мои заявки на отпуск</h2>

      <div className="controls" style={{ marginBottom: '20px', display: 'flex', justifyContent: 'space-between', alignItems: 'center', gap: '15px', flexWrap: 'wrap' }}>
        <div className="year-filter" style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <label htmlFor="vacation-year">Год:</label>
          <select id="vacation-year" value={year} onChange={handleYearChange} disabled={loading}>
            {/* Генерируем опции для нескольких лет */}
            {[...Array(5)].map((_, i) => {
              const y = new Date().getFullYear() + 2 - i;
              return <option key={y} value={y}>{y}</option>;
            })}
          </select>
        </div>
         <div className="status-filter" style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
            <label htmlFor="vacation-status">Статус:</label>
            <select id="vacation-status" value={statusFilter} onChange={handleStatusChange} disabled={loading}>
              <option value="">Все</option>
              {Object.entries(STATUS_MAP).map(([id, { text }]) => (
                <option key={id} value={id}>{text}</option>
              ))}
            </select>
          </div>
        <button onClick={() => fetchVacations(year, statusFilter)} className="btn btn-secondary" disabled={loading}>
          <FaSync className={loading ? 'spin-icon' : ''} /> Обновить
        </button>
      </div>

      {loading && <Loader text="Загрузка заявок..." />}

      {error && <div className="error-message">{error}</div>}

      {!loading && !error && vacations.length === 0 && (
        <p style={{ textAlign: 'center', color: 'var(--text-secondary)', marginTop: '30px' }}>
          Заявок на {year} год не найдено.
        </p>
      )}

      {!loading && !error && vacations.length > 0 && (
        <div className="vacation-table-container" style={{ overflowX: 'auto' }}>
          <table className="vacation-table" style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
            <thead>
              <tr>
                <th>ID</th>
                 {(isAdmin || isManager) && <th><FaUserTie /> Сотрудник</th>}
                <th>Год</th>
                <th>Статус</th>
                <th>Периоды</th>
                <th>Дней</th>
                <th>Комментарий</th>
                <th>Создана</th>
                <th style={{ minWidth: '120px' }}>Действия</th> 
              </tr>
            </thead>
            <tbody>
              {vacations.map((vacation) => {
                // Вычисляем totalDays и statusName здесь, перед рендерингом строки
                const periods = vacation.periods || [];
                const totalDays = periods.reduce((sum, p) => sum + (p.days_count || 0), 0);
                const statusInfo = STATUS_MAP[vacation.status_id] || { text: 'Неизвестно', Icon: FaHourglassHalf };
                const statusName = statusInfo.text; // Используем имя из карты

                return (
                  <motion.tr
                    key={vacation.id}
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ duration: 0.3 }}
                  >
                    <td>{vacation.id}</td>
                    {(isAdmin || isManager) && <td>{vacation.user_full_name || `User ${vacation.user_id || 'N/A'}`}</td>} 
                    <td>{vacation.year}</td>
                    {/* Передаем только statusId в StatusBadge, он сам найдет имя */}
                    <td><StatusBadge statusId={vacation.status_id} /></td> 
                    <td>
                      {periods.map((p, index) => (
                        <div key={p.id || index} style={{ whiteSpace: 'nowrap' }}>
                          {formatDate(p.start_date)} - {formatDate(p.end_date)} ({p.days_count || 0} дн.)
                        </div>
                      ))}
                      {periods.length === 0 && '-'}
                    </td>
                    <td>{totalDays}</td>
                    <td title={vacation.comment}>{vacation.comment ? `${vacation.comment.substring(0, 30)}${vacation.comment.length > 30 ? '...' : ''}` : '-'}</td>
                    <td style={{ whiteSpace: 'nowrap' }}>{formatDate(vacation.created_at)}</td>
                    <td className="action-buttons" style={{ whiteSpace: 'nowrap' }}>
                      {/* Пользователь может отменить свой Черновик или На рассмотрении */}
                      {user && vacation.user_id === user.id && (vacation.status_id === STATUS.DRAFT || vacation.status_id === STATUS.PENDING) && (
                        <button onClick={() => handleCancel(vacation.id)} className="btn btn-sm btn-warning" title={vacation.status_id === STATUS.DRAFT ? "Удалить черновик" : "Отменить заявку"} disabled={loading}>
                          {vacation.status_id === STATUS.DRAFT ? <FaTrashAlt /> : <FaBan />}
                        </button>
                      )}
                      {/* Менеджер/Админ могут управлять заявкой "На рассмотрении" */}
                      {canManageRequest(vacation.user_id) && vacation.status_id === STATUS.PENDING && (
                        <>
                          <button onClick={() => handleApprove(vacation.id)} className="btn btn-sm btn-success" title="Утвердить" disabled={loading}> <FaCheckCircle /> </button>
                          <button onClick={() => handleReject(vacation.id)} className="btn btn-sm btn-danger" title="Отклонить" disabled={loading}> <FaTimesCircle /> </button>
                        </>
                      )}
                      {/* Менеджер/Админ могут отменить Утвержденную заявку */}
                      {canManageRequest(vacation.user_id) && vacation.status_id === STATUS.APPROVED && (
                        <button onClick={() => handleCancel(vacation.id)} className="btn btn-sm btn-danger" title="Отменить утвержденную заявку" disabled={loading}> <FaBan /> </button>
                      )}
                      {/* Отображаем прочерк, если действий нет */}
                      {! (user && vacation.user_id === user.id && (vacation.status_id === STATUS.DRAFT || vacation.status_id === STATUS.PENDING)) &&
                       ! (canManageRequest(vacation.user_id) && vacation.status_id === STATUS.PENDING) &&
                       ! (canManageRequest(vacation.user_id) && vacation.status_id === STATUS.APPROVED) &&
                       '-'}
                    </td>
                  </motion.tr>
                );
              })}
            </tbody>
          </table>
        </div>
      )}

      {/* CSS для таблицы и статусов нужно будет добавить */}
      <style jsx>{`
        .vacation-table { border: 1px solid var(--border-color); }
        .vacation-table th, .vacation-table td { padding: 10px 12px; border: 1px solid var(--border-color); text-align: left; vertical-align: middle; }
                      </button>
                    )}
        .vacation-table th { background-color: var(--bg-tertiary); font-weight: 500; }
        .vacation-table tbody tr:hover { background-color: var(--bg-tertiary); }
        .status-badge { display: inline-flex; align-items: center; padding: 4px 8px; border-radius: var(--border-radius-md); font-size: 0.85rem; white-space: nowrap; }
        .status-draft { background-color: #6c757d; color: white; }
        .status-pending { background-color: var(--warning-color); color: #333; }
        .status-approved { background-color: var(--success-color); color: white; }
        .status-rejected { background-color: var(--danger-color); color: white; }
        .status-cancelled { background-color: #adb5bd; color: #333; }
        .action-buttons { display: flex; gap: 5px; }
        .btn-sm { padding: 4px 8px; font-size: 0.9rem; }
        .btn-sm svg { margin-right: 0; } /* Убираем отступ у иконок маленьких кнопок */
        .spin-icon { animation: spin 1.5s linear infinite; }
        @keyframes spin { to { transform: rotate(360deg); } }
      `}</style>

    </motion.div>
  );
};

export default VacationsList;
