import React, { useState, useEffect, useContext } from 'react';
import { motion } from 'framer-motion';
import { toast } from 'react-toastify';
import { 
  FaList, FaFilter, FaSync, FaEdit, FaTrashAlt, FaPaperPlane, 
  FaCheckCircle, FaTimesCircle, FaHourglassHalf, FaBan 
} from 'react-icons/fa';
import { 
  getMyVacations, 
  cancelVacationRequest, 
  approveVacationRequest, 
  rejectVacationRequest 
} from '../../api/vacations';
import Loader from '../../components/ui/Loader/Loader';
import { UserContext } from '../../context/UserContext';

// Функция для форматирования даты
const formatDate = (dateString) => {
  if (!dateString) return '-';
  try {
    return new Date(dateString).toLocaleDateString('ru-RU');
  } catch (e) {
    return dateString; // Возвращаем исходную строку в случае ошибки
  }
};

// Компонент для отображения статуса
const StatusBadge = ({ statusId }) => {
  let statusText = 'Неизвестно';
  let statusClass = 'status-unknown';
  let Icon = FaHourglassHalf;

  switch (statusId) {
    case 1: statusText = 'Черновик'; statusClass = 'status-draft'; Icon = FaEdit; break;
    case 2: statusText = 'На рассмотрении'; statusClass = 'status-pending'; Icon = FaHourglassHalf; break;
    case 3: statusText = 'Утверждена'; statusClass = 'status-approved'; Icon = FaCheckCircle; break;
    case 4: statusText = 'Отклонена'; statusClass = 'status-rejected'; Icon = FaTimesCircle; break;
    case 5: statusText = 'Отменена'; statusClass = 'status-cancelled'; Icon = FaBan; break;
    default: break;
  }

  return (
    <span className={`status-badge ${statusClass}`}>
      <Icon style={{ marginRight: '5px' }} />
      {statusText}
    </span>
  );
};

const VacationsList = () => {
  const [vacations, setVacations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [year, setYear] = useState(new Date().getFullYear()); // По умолчанию текущий год
  const [error, setError] = useState(null);
  const [filterStatus, setFilterStatus] = useState("all");

  const { user } = useContext(UserContext);
  const canManage = user && (user.role === 'admin' || user.role === 'manager');

  // Функция загрузки заявок
  const fetchVacations = async (selectedYear) => {
    setLoading(true);
    setError(null);
    try {
      const data = await getMyVacations(selectedYear); // Используем реальный API вызов
      // Сортируем заявки по дате создания (сначала новые)
      data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)); 
      setVacations(data);
    } catch (err) {
      setError(err.message || 'Не удалось загрузить список заявок.');
      toast.error(err.message || 'Не удалось загрузить список заявок.');
    } finally {
      setLoading(false);
    }
  };

  // Загрузка данных при монтировании и смене года
  useEffect(() => {
    fetchVacations(year);
  }, [year]);

  // Обработчик смены года
  const handleYearChange = (e) => {
    setYear(parseInt(e.target.value));
  };

  // Обработчик фильтрации по статусу
  const handleStatusFilterChange = (e) => {
    setFilterStatus(e.target.value);
  };

  // Фильтрация заявок согласно выбранному фильтру
  const filteredVacations = vacations.filter((vacation) => {
    if(filterStatus === "all") return true;
    if(filterStatus === "pending") return vacation.statusId === 2;
    if(filterStatus === "completed") return [3, 4, 5].includes(vacation.statusId);
    return true;
  });

  // Обработчик отмены заявки (для обычных пользователей)
  const handleCancel = async (id) => {
    if (window.confirm('Вы уверены, что хотите отменить эту заявку?')) {
      setLoading(true);
      try {
        await cancelVacationRequest(id);
        toast.success('Заявка успешно отменена');
        fetchVacations(year); 
      } catch (err) {
        toast.error(err.message || 'Не удалось отменить заявку.');
        setLoading(false);
      }
    }
  };

  // Обработчик утверждения заявки (для администраторов/менеджеров)
  const handleApprove = async (id) => {
    if (window.confirm('Подтверждаете утверждение заявки?')) {
      setLoading(true);
      try {
        await approveVacationRequest(id);
        toast.success('Заявка утверждена');
        fetchVacations(year);
      } catch (err) {
        toast.error(err.message || 'Ошибка утверждения заявки');
      } finally {
        setLoading(false);
      }
    }
  };

  // Обработчик отклонения заявки (для администраторов/менеджеров)
  const handleReject = async (id) => {
    if (window.confirm('Подтверждаете отклонение заявки? Дни будут возвращены сотруднику.')) {
      setLoading(true);
      try {
        await rejectVacationRequest(id);
        toast.success('Заявка отклонена, дни возвращены');
        fetchVacations(year);
      } catch (err) {
        toast.error(err.message || 'Ошибка отклонения заявки');
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <motion.div
      className="vacations-list-container card"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <h2><FaList /> Мои заявки на отпуск</h2>

      <div 
        className="controls" 
        style={{ 
          marginBottom: '20px', 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center', 
          gap: '15px', 
          flexWrap: 'wrap' 
        }}
      >
        <div className="year-filter" style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <label htmlFor="vacation-year">Год:</label>
          <select id="vacation-year" value={year} onChange={handleYearChange} disabled={loading}>
            {[...Array(5)].map((_, i) => {
              const y = new Date().getFullYear() + 2 - i;
              return <option key={y} value={y}>{y}</option>;
            })}
          </select>
        </div>
        <div className="status-filter" style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <label htmlFor="vacation-status">Статус:</label>
          <select id="vacation-status" value={filterStatus} onChange={handleStatusFilterChange} disabled={loading}>
            <option value="all">Все</option>
            <option value="pending">Ожидающие</option>
            <option value="completed">Выполненные</option>
          </select>
        </div>
        <button onClick={() => fetchVacations(year)} className="btn btn-secondary" disabled={loading}>
          <FaSync className={loading ? 'spin-icon' : ''} /> Обновить
        </button>
      </div>

      {loading && <Loader text="Загрузка заявок..." />}

      {error && <div className="error-message">{error}</div>}

      {!loading && !error && filteredVacations.length === 0 && (
        <p style={{ textAlign: 'center', color: 'var(--text-secondary)', marginTop: '30px' }}>
          Заявок на {year} год не найдено.
        </p>
      )}

      {!loading && !error && filteredVacations.length > 0 && (
        <div className="vacation-table-container" style={{ overflowX: 'auto' }}>
          <table className="vacation-table" style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Год</th>
                {canManage && <th>Сотрудник</th>}
                <th>Статус</th>
                <th>Периоды</th>
                <th>Дней</th>
                <th>Комментарий</th>
                <th>Создана</th>
                <th>Действия</th>
              </tr>
            </thead>
            <tbody>
              {filteredVacations.map((vacation) => (
                <motion.tr 
                  key={vacation.id}
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ duration: 0.3 }}
                >
                  <td>{vacation.id}</td>
                  <td>{vacation.year}</td>
                  {canManage && <td>{vacation.userName || '-'}</td>}
                  <td><StatusBadge statusId={vacation.statusId} /></td>
                  <td>
                    {vacation.periods.map((p, index) => (
                      <div key={p.id || index} style={{ whiteSpace: 'nowrap' }}>
                        {formatDate(p.startDate)} - {formatDate(p.endDate)}
                      </div>
                    ))}
                  </td>
                  <td>{vacation.periods.reduce((sum, p) => sum + p.daysCount, 0)}</td>
                  <td>{vacation.comment || '-'}</td>
                  <td style={{ whiteSpace: 'nowrap' }}>{formatDate(vacation.createdAt)}</td>
                  <td className="action-buttons" style={{ whiteSpace: 'nowrap' }}>
                    {canManage ? (
                      vacation.statusId === 2 ? (
                        <>
                          <button onClick={() => handleApprove(vacation.id)} className="btn btn-sm btn-success" title="Утвердить заявку" disabled={loading}> <FaCheckCircle /> </button>
                          <button onClick={() => handleReject(vacation.id)} className="btn btn-sm btn-danger" title="Отклонить заявку" disabled={loading}> <FaTimesCircle /> </button>
                        </>
                      ) : (
                        <button className="btn btn-sm btn-info" title="Просмотр заявки">Просмотр</button>
                      )
                    ) : (
                      <>
                        {vacation.statusId === 1 && ( // Черновик
                          <>
                            <button className="btn btn-sm btn-primary" title="Редактировать" disabled> <FaEdit /> </button>
                            <button className="btn btn-sm btn-success" title="Отправить" disabled> <FaPaperPlane /> </button>
                            <button onClick={() => handleCancel(vacation.id)} className="btn btn-sm btn-danger" title="Удалить черновик" disabled={loading}> <FaTrashAlt /> </button>
                          </>
                        )}
                        {vacation.statusId === 2 && ( // На рассмотрении
                          <button onClick={() => handleCancel(vacation.id)} className="btn btn-sm btn-warning" title="Отменить заявку" disabled={loading}> <FaBan /> </button>
                        )}
                      </>
                    )}
                  </td>
                </motion.tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      <style jsx>{`
        .vacation-table { border: 1px solid var(--border-color); }
        .vacation-table th, .vacation-table td { padding: 10px 12px; border: 1px solid var(--border-color); text-align: left; vertical-align: middle; }
        .vacation-table th { background-color: var(--bg-tertiary); font-weight: 500; }
        .vacation-table tbody tr:hover { background-color: var(--bg-tertiary); }
        .status-badge { display: inline-flex; align-items: center; padding: 4px 8px; border-radius: var(--border-radius-md); font-size: 0.85rem; white-space: nowrap; }
        .status-draft { background-color: #6c757d; color: white; }
        .status-pending { background-color: var(--warning-color); color: #333; }
        .status-approved { background-color: var(--success-color); color: white; }
        .status-rejected { background-color: var(--danger-color); color: white; }
        .status-cancelled { background-color: #adb5bd; color: #333; }
        .action-buttons { display: flex; gap: 5px; }
        .btn-sm { padding: 4px 8px; font-size: 0.9rem; }
        .btn-sm svg { margin-right: 0; }
        .spin-icon { animation: spin 1.5s linear infinite; }
        @keyframes spin { to { transform: rotate(360deg); } }
      `}</style>
    </motion.div>
  );
};

export default VacationsList;
