import React, { useState, useEffect } from 'react'; // Убран useCallback и flushSync
import { motion, AnimatePresence } from 'framer-motion';
import DatePicker, { registerLocale } from 'react-datepicker';
import { toast } from 'react-toastify';
import { FaCalendarAlt, FaPlus, FaTrash, FaSave, FaPaperPlane } from 'react-icons/fa';
import ru from 'date-fns/locale/ru';
import { getVacationLimit, createVacationRequest, submitVacationRequest } from '../../api/vacations'; // Предполагается, что API файл будет создан
import 'react-datepicker/dist/react-datepicker.css';
import './VacationForm.css';

// Регистрация русской локали для DatePicker
registerLocale('ru', ru);

const VacationForm = () => {
  const [year, setYear] = useState(new Date().getFullYear() + 1);
  const [periods, setPeriods] = useState([{ startDate: null, endDate: null, daysCount: 0 }]);
  const [status, setStatus] = useState('draft'); // 'draft', 'submitted'
  const [limit, setLimit] = useState(28);
  const [usedDays, setUsedDays] = useState(0);
  const [loading, setLoading] = useState(false); // Для загрузки лимита
  const [submitting, setSubmitting] = useState(false); // Для сохранения/отправки
  const [errors, setErrors] = useState({});
  const [requestId, setRequestId] = useState(null); // ID сохраненной заявки


  // Загрузка лимита отпуска при изменении года
  useEffect(() => {
    const fetchLimit = async () => {
      try {
        setLoading(true);
        const limitData = await getVacationLimit(year); // Используем реальный API вызов
        setLimit(limitData.totalDays);
        setUsedDays(limitData.usedDays);
      } catch (error) {
        // Обработка ошибки, если лимит не найден (например, для нового пользователя/года)
        if (error.message.includes("лимит отпуска не найден")) {
            toast.info(`Лимит на ${year} год еще не установлен. Используется стандартный лимит (${limit} дней).`);
            // Оставляем дефолтные значения limit и usedDays
        } else {
            toast.error("Ошибка при загрузке лимита отпуска"); // Показываем общую ошибку, если это не ошибка "лимит не найден"
        }
        // toast.error("Ошибка при загрузке лимита отпуска"); // Убираем дублирующийся вызов
        console.error("Ошибка при загрузке лимита:", error); // Логируем ошибку для отладки
      } finally {
        setLoading(false);
      }
    };

    fetchLimit();
  }, [year]);

  // Вычисление общего количества дней
  const totalDaysRequested = periods.reduce((sum, period) => sum + period.daysCount, 0);
  const remainingDays = limit - usedDays - totalDaysRequested;

  // Функция для подсчета дней между датами
  const calculateDays = (startDate, endDate) => {
    if (!startDate || !endDate || startDate > endDate) return 0; // Добавлена проверка startDate > endDate
    const diffTime = Math.abs(endDate.getTime() - startDate.getTime());
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
  };

  // Обработка изменения года
  const handleYearChange = (e) => {
    setYear(parseInt(e.target.value));
    // Сброс периодов и ошибок при смене года
    setPeriods([{ startDate: null, endDate: null, daysCount: 0 }]);
    setErrors({});
    setRequestId(null);
    setStatus('draft');
  };

  // Обработка изменения дат
  const handleDateChange = (index, field, date) => {
    const newPeriods = [...periods];
    newPeriods[index][field] = date;

    // Автоматический расчет дней, если обе даты выбраны и start <= end
    if (newPeriods[index].startDate && newPeriods[index].endDate && newPeriods[index].startDate <= newPeriods[index].endDate) {
      newPeriods[index].daysCount = calculateDays(
        newPeriods[index].startDate,
        newPeriods[index].endDate
      );
    } else {
      newPeriods[index].daysCount = 0; // Сброс, если даты некорректны
    }

    setPeriods(newPeriods);
    validateForm(newPeriods); // Валидация при каждом изменении
  };

  // Добавление нового периода
  const addPeriod = () => {
    setPeriods([...periods, { startDate: null, endDate: null, daysCount: 0 }]);
  };

  // Удаление периода
  const removePeriod = (index) => {
    const newPeriods = periods.filter((_, i) => i !== index);
    setPeriods(newPeriods);
    validateForm(newPeriods); // Валидация после удаления
  };

  // Валидация формы (переработанная логика)
  // Убран useCallback
  const validateForm = (periodsToValidate = periods) => { // Снова принимаем аргумент или используем состояние
    // Всегда начинаем с пустого объекта ошибок
    const currentErrors = {}; 
    // const periodsToValidate = periods; // Используем переданный аргумент или состояние

    // 1. Проверка базовой корректности дат и наличия периодов
    if (periodsToValidate.length === 0) {
      currentErrors.noPeriods = 'Необходимо добавить хотя бы один период отпуска.';
      // Устанавливаем ошибки и выходим, если периодов нет
      setErrors(currentErrors);
      return false;
    }

    const hasInvalidDates = periodsToValidate.some(
      period => !period.startDate || !period.endDate || period.startDate > period.endDate
    );
    if (hasInvalidDates) {
      currentErrors.invalidDates = 'Все даты должны быть заполнены корректно (дата начала <= дата окончания).';
      // Не выходим сразу, чтобы проверить и другие ошибки, если возможно
    }

    // 2. Проверка общих дней и правила 14 дней (только если базовые даты корректны)
    if (!hasInvalidDates) {
      const totalRequested = periodsToValidate.reduce((sum, period) => sum + period.daysCount, 0);

      if (totalRequested <= 0) {
           currentErrors.noDays = 'Необходимо запросить хотя бы один день отпуска (проверьте даты).';
      } else { // totalRequested > 0
          // Отладка: выводим периоды и рассчитанные дни
          console.log('Validating 14-day rule. Periods:', JSON.stringify(periodsToValidate.map(p => ({ start: p.startDate, end: p.endDate, days: p.daysCount }))));

          // Проверяем правило 14 дней
          const hasLongPeriod = periodsToValidate.some(period => period.daysCount >= 14);
          console.log('Has long period (>= 14 days):', hasLongPeriod); // Отладка
          // Добавляем ошибку только если условие НЕ выполнено
          if (!hasLongPeriod) { 
              currentErrors.longPeriod = 'Одна из частей отпуска должна быть не менее 14 календарных дней.';
          }
          // Проверяем лимит
          if (totalRequested > (limit - usedDays)) {
              currentErrors.limit = `Превышен доступный лимит дней отпуска (${limit - usedDays} дн.).`;
          }
      }
    }

    // Log the errors object *before* setting state
    console.log("Errors object calculated:", JSON.stringify(currentErrors));

    // Простое обновление состояния
    setErrors({ ...currentErrors }); 

    // Log *after* setting state (хотя это может не показать обновленное значение немедленно)
    // console.log("Called setErrors with:", JSON.stringify(currentErrors)); // Можно убрать этот лог

    const isValid = Object.keys(currentErrors).length === 0;
    console.log(`validateForm returning: ${isValid}`); // Log the return value
    return isValid; // Проверяем собранный объект
  };

  // Убран useEffect для валидации

  // Сохранение заявки
  const handleSave = async (e) => {
    e.preventDefault();

    // Вызываем validateForm() непосредственно перед сохранением для актуальной проверки
    const isFormValid = validateForm(); // Сохраним результат
    if (!isFormValid) { 
        // Логируем *текущее* состояние errors, которое привело к false
        console.log("Save blocked by validateForm() check. Current errors state:", JSON.stringify(errors)); 
        // Временно убираем toast, чтобы проверить, появляется ли ошибка в UI без него
        // toast.warn('Пожалуйста, исправьте ошибки в форме перед сохранением.'); 
        return;
    }

    // Если валидация пройдена, продолжаем
    console.log("Validation passed in handleSave, proceeding.");
    try {
      setSubmitting(true);

      const vacationRequest = {
        year,
        periods: periods.map(period => ({
          // Форматирование дат перед отправкой (пример)
          startDate: period.startDate.toISOString().split('T')[0], 
          endDate: period.endDate.toISOString().split('T')[0],
          daysCount: period.daysCount
        })),
        statusId: 1 // Черновик
      };

      // Используем реальный API вызов
      // TODO: Определить, нужно ли обновлять или создавать новую заявку, если requestId уже есть
      let response;
      if (requestId) {
         // Логика обновления существующего черновика (если API поддерживает)
         // response = await updateVacationRequest(requestId, vacationRequest); 
         // Пока просто создаем новую, если сохраняем повторно
         response = await createVacationRequest(vacationRequest); 
         toast.info('Черновик обновлен (создана новая версия).'); // Уточняющее сообщение
      } else {
         response = await createVacationRequest(vacationRequest);
         toast.success('Заявка успешно сохранена как черновик');
      }

      setRequestId(response.id); // Сохраняем ID созданной/обновленной заявки
      setStatus('draft'); // Убедимся, что статус - черновик

    } catch (error) {
      toast.error(error.message || 'Ошибка при сохранении заявки');
    } finally {
      setSubmitting(false);
    }
  };

  // Отправка заявки руководителю
  const handleSubmit = async () => {
    // Более явная проверка перед отправкой
    if (!requestId) {
      toast.warn('Сначала необходимо сохранить заявку как черновик.'); // Изменено на warn
      return;
    }
    // Вызываем validateForm() непосредственно перед отправкой для актуальной проверки
     if (!validateForm()) { 
        console.log("Submit blocked by validateForm() check.");
        toast.warn('Пожалуйста, исправьте ошибки в форме перед отправкой');
        return;
    }

    // Если валидация пройдена, продолжаем
    console.log("Validation passed in handleSubmit, proceeding.");
    try {
      setSubmitting(true);
      await submitVacationRequest(requestId); // Используем реальный API вызов
      toast.success('Заявка успешно отправлена руководителю');
      setStatus('submitted'); // Меняем статус в UI
    } catch (error) {
      toast.error(error.message || 'Ошибка при отправке заявки');
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <motion.div
      className="vacation-form-container"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <h2>Оформление отпуска на {year} год</h2>

      <div className="year-selector">
        <label htmlFor="year">Выберите год:</label>
        <select id="year" value={year} onChange={handleYearChange} disabled={loading || submitting}>
          <option value={new Date().getFullYear()}>Текущий год</option>
          <option value={new Date().getFullYear() + 1}>Следующий год</option>
        </select>
        {loading && <span className="loading-indicator"> Загрузка лимита...</span>}
      </div>

      <motion.div 
        className="vacation-limits card" // Добавлен класс card
        initial={{ x: -20, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ delay: 0.2 }}
      >
        <div className="limit-item">
          <span>Доступно дней:</span>
          <span className="limit-value">{limit}</span>
        </div>
        <div className="limit-item">
          <span>Использовано:</span>
          <span className="limit-value">{usedDays}</span>
        </div>
        <div className="limit-item">
          <span>Запрошено:</span>
          <span className="limit-value">{totalDaysRequested}</span>
        </div>
        <div className="limit-item">
          <span>Осталось:</span>
          <span className={`limit-value ${remainingDays < 0 ? 'error' : ''}`}>
            {remainingDays}
          </span>
        </div>
      </motion.div>

      {/* Отображение ошибок (изменено на явные проверки) */}
      {errors.invalidDates && <div className="error-message">{errors.invalidDates}</div>}
      {errors.noPeriods && <div className="error-message">{errors.noPeriods}</div>}
      {errors.noDays && <div className="error-message">{errors.noDays}</div>}
      {/* Упрощенное отображение ошибки без анимации */}
      {/* Убран console.log из рендера */}
      {errors.longPeriod ? <div className="error-message">{errors.longPeriod}</div> : null} 
      {errors.limit && <div className="error-message">{errors.limit}</div>}

      <form onSubmit={handleSave}>
        {/* <AnimatePresence> */} {/* Временно убираем AnimatePresence */}
          {periods.map((period, index) => (
            <motion.div 
              key={index}
              className="vacation-period card" // Добавлен класс card
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ delay: index * 0.1 }}
            >
              <h3>Часть отпуска {index + 1}</h3>

              <div className="date-inputs">
                <div className="date-field">
                  <label htmlFor={`start-date-${index}`}>Дата начала</label>
                  <div className="date-picker-container">
                    <DatePicker
                      id={`start-date-${index}`}
                      selected={period.startDate}
                      onChange={(date) => handleDateChange(index, 'startDate', date)}
                      selectsStart
                      startDate={period.startDate}
                      endDate={period.endDate}
                      minDate={new Date(year, 0, 1)}
                      maxDate={new Date(year, 11, 31)}
                      dateFormat="dd.MM.yyyy"
                      locale="ru"
                      placeholderText="Выберите дату"
                      className="date-input"
                      disabled={submitting || status === 'submitted'}
                    />
                    <FaCalendarAlt className="date-icon" />
                  </div>
                </div>

                <div className="date-field">
                  <label htmlFor={`end-date-${index}`}>Дата окончания</label>
                  <div className="date-picker-container">
                    <DatePicker
                      id={`end-date-${index}`}
                      selected={period.endDate}
                      onChange={(date) => handleDateChange(index, 'endDate', date)}
                      selectsEnd
                      startDate={period.startDate}
                      endDate={period.endDate}
                      minDate={period.startDate || new Date(year, 0, 1)}
                      maxDate={new Date(year, 11, 31)}
                      dateFormat="dd.MM.yyyy"
                      locale="ru"
                      placeholderText="Выберите дату"
                      className="date-input"
                      disabled={submitting || status === 'submitted' || !period.startDate} // Блокируем, пока не выбрана дата начала
                    />
                    <FaCalendarAlt className="date-icon" />
                  </div>
                </div>
              </div>

              <div className="days-count">
                Количество дней: <strong>{period.daysCount}</strong>
              </div>

              {periods.length > 1 && (
                <motion.button
                  type="button"
                  className="remove-period btn btn-danger" // Добавлены классы btn
                  onClick={() => removePeriod(index)}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  disabled={submitting || status === 'submitted'}
                >
                  <FaTrash /> Удалить часть
                </motion.button>
              )}
            </motion.div>
          ))}
        {/* </AnimatePresence> */} {/* Временно убираем AnimatePresence */}

        <div className="form-actions">
          <motion.button
            type="button"
            className="add-period btn" // Добавлен класс btn
            onClick={addPeriod}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            disabled={submitting || status === 'submitted'}
          >
            <FaPlus /> Добавить часть отпуска
          </motion.button>

          <motion.button
            type="submit"
            className="save-request btn btn-primary" // Добавлены классы btn
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            disabled={
              submitting || 
              status === 'submitted' || 
              (() => { // IIFE для логирования
                const hasErrors = Object.keys(errors).length > 0;
                console.log(`Save button disabled check: errors=${JSON.stringify(errors)}, hasErrors=${hasErrors}`);
                return hasErrors;
              })()
            } // Блокируем при ошибках
          >
            <FaSave /> {requestId ? 'Обновить черновик' : 'Сохранить черновик'}
          </motion.button>

          <motion.button
            type="button"
            className="submit-request btn btn-success" // Добавлены классы btn
            onClick={handleSubmit}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            disabled={submitting || !requestId || status === 'submitted' || Object.keys(errors).length > 0} // Блокируем при ошибках
          >
            <FaPaperPlane /> Отправить руководителю
          </motion.button>
        </div>
      </form>

      {status === 'submitted' && (
          <motion.div 
            className="success-message" // Нужен CSS для этого класса
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
          >
            Заявка успешно отправлена!
          </motion.div>
      )}
    </motion.div>
  );
};

